import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Course from './Course';


const App = () => {
  const [persons , setPersons] = useState([])
  const [newName , setNewName] = useState('');
  const [newNumber , setNewNumber] = useState('');



  const handleInputName = (event) =>{
    setNewName(event.target.value);
  }
  const handleInputNum = (event) =>{
    setNewNumber(event.target.value);
  }


  const addNote = (event) =>{
    event.preventDefault()
    if(newName.length < 1 || newNumber.length < 1) return
    if(persons.filter(person => person.name === newName).length > 0){
      window.alert(`${newName} already exists`)
      return;
    }
    const newObj = {
        name : newName,
        number : newNumber
    }

    setPersons(persons.concat(newObj))
    setNewName(''); 
    setNewNumber('')
  }

  return (
    <div>
      <h2>PhoneBook</h2>
      <form onSubmit = {addNote}>
        <div>
          name : <input value = {newName} onChange={handleInputName} />
          number : <input value = {newNumber} onChange={handleInputNum} />
        </div>
        <br/>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        <ul>
        {persons.map(person =>
          <Note key = {person.name} person = {person}/>)
          }
        </ul>
      </div>
    </div>
  )
}

const Note = ({person}) =>{
  return (
    <li>{person}</li>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
