import axios from "axios";

const baseURL = 'http://localhost:3001/persons';

const getAll = () =>{
    const request = axios.get(baseURL);
    return request.then(response =>{
        return response.data;
    })
}
const create = newObj =>{
    return axios.post(baseURL , newObj)
            .then(response => {
                return response.data;
            })
}

export default {getAll};
