import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Course from './Course';


const App = () => {
  const [persons , setPersons] = useState([]);
  const [newName , setNewName] = useState('');
  const [newNumber , setNewNumber] = useState('');
  const [newFilter , setNewFilter] = useState('');
  const [allPersons, setAllPersons] = useState([]);


  const handleInputName = (event) =>{
    setNewName(event.target.value);
  }
  const handleInputNum = (event) =>{
    setNewNumber(event.target.value);
  }


  const addNote = (event) =>{
    event.preventDefault()
    if(newName.length < 1 || newNumber.length < 1) return
    if(allPersons.filter(person => person.name === newName).length > 0){
      window.alert(`${newName} already exists`)
      return;
    }
    const newObj = {
        name : newName,
        number : newNumber
    }

    setAllPersons(allPersons.concat(newObj))
    setNewName(''); 
    setNewNumber('')
  }
  const handleNewFilter = (event) =>{
    setNewFilter(event.target.value)
    console.log(newFilter);
    const filtered =  allPersons.filter(person => person.name.includes(newFilter));
    console.log(filtered);
    console.log(filtered.length);
    setPersons(filtered);

  }

  return (
    <div>

      <h2>PhoneBook</h2>
      <div>
        Filter shown with
        <input value ={newFilter} onChange={handleNewFilter}/>
      </div>
      <form onSubmit = {addNote}>
        <div>
          name : <input value = {newName} onChange={handleInputName} />
          number : <input value = {newNumber} onChange={handleInputNum} />
        </div>
        <br/>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        <ul>
          <Display persons = {persons} allpersons = {allPersons} newFilter 
              = {newFilter}/> 
        </ul>
      </div>
    </div>
  )
}

const Note = ({person}) =>{
  return (
    <li>{person.name} {person.number}</li>
  )
}
const Display = ({persons, allpersons, newFilter}) => {
    if(newFilter.length ===0 ){
      return(
        allpersons.map(person =>
          <Note key = {person.name} person = {person}/>)
      ) 
    }
    return (persons.map(person =>
       <Note key = {person.name} person = {person}/>)
    )
}
ReactDOM.render(<App />, document.getElementById('root'))
