const Notification = ({message}) =>{
    if(message ===null){
        return null;
    }
    const notifStyle ={
        color: 'green',
        background: 'lightgrey',
        fontSize: 20,
        borderStyle: 'solid',
        borderRadius: 5,
        padding: 10,
        marginBottom: 10,
    }

    return(
        <div style ={notifStyle}>
            {message}
        </div>
    )
}
export default Notification;
